document.getElementById("create-btn-id").addEventListener("click",(e=>{location.href="../create"})),document.getElementById("page-title-id").addEventListener("click",(e=>{location.href="../"}));const what_info=document.getElementById("what-info"),benefits_info=document.getElementById("benefits-info"),how_info=document.getElementById("how-info");function openAllCards(){what_info.style.height=what_info.scrollHeight+"px",benefits_info.style.height=benefits_info.scrollHeight+"px",how_info.style.height=how_info.scrollHeight+"px"}document.getElementById("what-toggle").addEventListener("click",(e=>{const t=what_info.style.height;""==t||"0px"==t?openAllCards():what_info.style.height="0px"})),document.getElementById("benefits-toggle").addEventListener("click",(e=>{const t=benefits_info.style.height;""==t||"0px"==t?openAllCards():benefits_info.style.height="0px"})),document.getElementById("how-toggle").addEventListener("click",(e=>{const t=how_info.style.height;""==t||"0px"==t?(openAllCards(),how_info.style.height=how_info.scrollHeight+"px"):how_info.style.height="0px"}));